import 'package:flutter/material.dart';
import '13.1_detail.dart';

void main() {
  runApp(new MaterialApp(
    title: "Sidebar",
    home: new Home(),
  ));
}

class Home extends StatefulWidget {
  @override
  _HomeState createState() => _HomeState();
}

class _HomeState extends State<Home> {
  String gambar1 =
      "data:image/jpeg;base64,";
  String gambar2 =
      "data:image/jpeg;base64,";
  String backup;

  String nama1 = "Indra Armaula";
  String nama2 = "Jack Sparrow";
  String backupnama;

  void gantiuser() {
    this.setState(() {
      backup = gambar1;
      gambar1 = gambar2;
      gambar2 = backup;

      backupnama = nama1;
      nama1 = nama2;
      nama2 = backupnama;
    });
  }

  @override
  Widget build(BuildContext context) {
    return new Scaffold(
      appBar: new AppBar(
        title: new Text("Demo Sidebar (DRAWER)"),
        backgroundColor: Colors.red[700],
      ),
      drawer: new Drawer(
        child: new ListView(
          children: <Widget>[
            new UserAccountsDrawerHeader(
              accountName: new Text(nama1),
              accountEmail: new Text("idcorner@gmail.com"),
              currentAccountPicture: new GestureDetector(
                onTap: () {
                  Navigator.of(context).pop();
                  Navigator.of(context).push(new MaterialPageRoute(
                      builder: (BuildContext context) =>
                          new Detail(nama: nama1, gambar: gambar1)));
                },
                // onTap: () => Navigator.of(context).push(new MaterialPageRoute(
                //     builder: (BuildContext context) =>
                //         new Detail(nama: nama1, gambar: gambar1))),
                child: new CircleAvatar(
                  backgroundImage: new NetworkImage(gambar1),
                ),
              ),
              decoration: new BoxDecoration(
                image: new DecorationImage(
                    image: new NetworkImage(gambar2), fit: BoxFit.cover),
              ),
              otherAccountsPictures: <Widget>[
                new GestureDetector(
                    onTap: () => gantiuser(),
                    child: new CircleAvatar(
                      backgroundImage: new NetworkImage(
                          "data:image/png;base64,"),
                    ))
              ],
            ),
            new ListTile(
              title: new Text("Setting"),
              trailing: new Icon(Icons.settings),
            ),
            new ListTile(
              title: new Text("Close"),
              trailing: new Icon(Icons.close),
            )
          ],
        ),
      ),
      body: new Container(),
    );
  }
}
